class Solution1:
    def words_in_str(self, s, wordDict):
        words = []
        for word in wordDict:
            if word in s:
                words.append(word)
        return words
    
    def remove_first_instance(self, s, word):
        length = len(word)
        for i in range(0,len(s)-length+1):
            if s[i:i+length] == word:
                return s[0:i]+"*"+s[i+length:]
    
    def all_stars(self, st):
        for char in st:
            if char != "*":
                return False
        return True

    def wordBreak(self, s: str, wordDict: list[str]) -> bool:
        if not s:
            return True
        words = self.words_in_str(s=s,wordDict=wordDict)
        if words:
            b = False
            for word in words:
                b = b or self.wordBreak(self.remove_first_instance(s=s, word=word),wordDict=words)
            return b
        return False
    
class Solution:
    def __init__(self) -> None:
        self.starts_ends = dict()
        self.possible = False

    def compute_intervals(self, s, word):
        length = len(word)
        for i in range(0,len(s)-length+1):
            if s[i:i+length] == word:
                if i not in self.starts_ends:
                    self.starts_ends[i] = [i+length]
                else:
                    self.starts_ends[i].append(i+length)
                    self.starts_ends[i].append(i+length)
                if i+length == len(s):
                    self.possible = True
    
    def words_in_str(self, s, wordDict):
        words = []
        for word in wordDict:
            if word in s:
                words.append(word)
        return words
    
    def reachable(self, curr_idx):
        if curr_idx == len(self.s):
            return True 
        if curr_idx not in self.starts_ends:
            return False
        b = False
        for end_idx in self.starts_ends[curr_idx]:
            b = b or self.reachable(end_idx)
        return b

            
    def wordBreak(self, s, wordDict):
        self.s = s
        words = self.words_in_str(s=s,wordDict=wordDict)
        for word in words:
            self.compute_intervals(s, word)
        
        if not self.possible: return False
        return self.reachable(0)



